import React, { useState } from 'react'
import './Header.css'
import PopupIconButton from './components/popupiconbutton'
import NotificationButton from './components/notificationbutton'
import InfoButton from './components/infobutton'
import { AppBar, Backdrop, Fade, MenuItem, Toolbar, Button, Typography, IconButton, makeStyles, ListSubheader, Divider, List, ListItem, ListItemIcon, ListItemText, Hidden, Drawer } from '@material-ui/core'
import { Menu, Person as PersonIcon, InfoRounded as InfoLogo, Notifications as BellIcon, Dashboard as DshIcon, Receipt as ReceiptIcon, Height } from '@material-ui/icons'
import ExitToAppIcon from '@material-ui/icons/ExitToApp';

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    backgroundColor: '#fafafa',
    boxShadow: '0px 1px #f0f0f0;',
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    color: theme.palette.info.dark,
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  toolbar: {
    ...theme.mixins.toolbar,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '80vh'
  },
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  logo: {
    fontWeight: 'bolder',
  },
  button: {
    textTransform: 'none',
    marginLeft: '20px',
    marginRight: '20px',
    backgroundColor: theme.palette.info.dark,
    color: '#ffffff',
    borderRadius: '8px'
  },
  iconButton: {
    color: '#7C8DB5'
  },
  backdrop: {
    zIndex: theme.zIndex.drawer + 1,
    color: '#fff',
  },
  selectedMenu: {
    color: theme.palette.info.dark,
    borderRight: '3px solid ' + theme.palette.info.dark,
  },
  selectedIcon: {
    color: theme.palette.info.dark,
  },
  profileText:{
    fontSize:'14px',
    color: '#7C8DB5'
  }
}))

function Header(props) {
  const { window } = props;
  const classes = useStyles();


  const [showBackdrop, setShowBackdrop] = useState(false)
  const [mobileOpen, setMobileOpen] = React.useState(false)
  const container = window !== undefined ? () => window().document.body : undefined

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen)
  };

  const drawer = (
    <div>
      <div className={classes.toolbar} >
        <Typography variant="h5" className={classes.logo}>
          LOGO
        </Typography>
      </div>
      <Divider />
      <List subheader={
        <ListSubheader component="div" id="nested-list-subheader">
          Heading 1
        </ListSubheader>
      }>
        <ListItem button className={classes.selectedMenu}>
          <ListItemIcon> <DshIcon className={classes.selectedIcon} /> </ListItemIcon>
          <ListItemText primary="Menu Option 1" />
        </ListItem>
        <ListItem button>
          <ListItemIcon> <ReceiptIcon /> </ListItemIcon>
          <ListItemText primary="Menu Option 2" />
        </ListItem>
        <ListItem button>
          <ListItemIcon> <ReceiptIcon /> </ListItemIcon>
          <ListItemText primary="Menu Option 3" />
        </ListItem>
      </List>
      <Divider />
      <List subheader={
        <ListSubheader component="div" id="nested-list-subheader">
          Heading 2
        </ListSubheader>
      }>
        <ListItem button>
          <ListItemIcon> <DshIcon /> </ListItemIcon>
          <ListItemText primary="Menu Option 4" />
        </ListItem>
        <ListItem button>
          <ListItemIcon> <ReceiptIcon /> </ListItemIcon>
          <ListItemText primary="Menu Option 5" />
        </ListItem>
      </List>
      <Divider />
      <List subheader={
        <ListSubheader component="div" id="nested-list-subheader">
          Heading 3
        </ListSubheader>
      }>
        <ListItem button>
          <ListItemIcon> <DshIcon /> </ListItemIcon>
          <ListItemText primary="Menu Option 6" />
        </ListItem>
        <ListItem button>
          <ListItemIcon> <ReceiptIcon /> </ListItemIcon>
          <ListItemText primary="Menu Option 7" />
        </ListItem>
      </List>
      <Divider />
      <List subheader={
        <ListSubheader component="div" id="nested-list-subheader">
